Ôªø// VolumeColorVsSMA ‚Äì v2.3 (simple constant gate)
// Blue histogram bar when bar volume > VolSmaMult √ó 50‚ÄëSMA of volume (default 0.9).
// Yellow line plots the 50‚ÄëSMA itself.


#region Using declarations
using System;
using System.ComponentModel.DataAnnotations;
using NinjaTrader.NinjaScript;
using NinjaTrader.NinjaScript.Indicators;
using NinjaTrader.Data;
using System.Windows.Media;
#endregion


namespace NinjaTrader.NinjaScript.Indicators
{
    public class VolumeColorVsSMA : Indicator
    {
        private SMA volSma;


        // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ USER PARAMETER ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
        [NinjaScriptProperty, Range(0.1, 5)]
        [Display(Name = "Vol SMA Mult", Order = 0, GroupName = "Parameters")]
        public double VolSmaMult { get; set; } = 0.9;


        protected override void OnStateChange()
        {
            if (State == State.SetDefaults)
            {
                Name        = "VolumeColorVsSMA";
                Description = "Colours volume bars blue when barVol exceeds VolSmaMult √ó 50‚ÄëSMA (default 0.9); red otherwise. Yellow line = 50‚ÄëSMA.";
                Calculate   = Calculate.OnBarClose;
                IsOverlay   = false;


                AddPlot(Brushes.Yellow, "VolumeSMA");   // idx 0 ‚Äì SMA line
                AddPlot(Brushes.Gray,   "VolumeBars");  // idx 1 ‚Äì histogram
                Plots[1].PlotStyle = PlotStyle.Bar;
            }
            else if (State == State.Configure)
            {
                volSma = SMA(Volume, 50);
            }
        }


        protected override void OnBarUpdate()
        {
            if (CurrentBar < 50) return;


            double smaVal = volSma[0];
            double barVol = Volume[0];


            Values[0][0] = smaVal;   // SMA plot
            Values[1][0] = barVol;   // histogram bar


            PlotBrushes[1][0] = barVol > smaVal * VolSmaMult ? Brushes.DodgerBlue : Brushes.Red;
        }
    }
}


